    public static function getAttributeList($attr) {
        $list = array();
        foreach(self::$metadata as $shopid => $meta) {
            if(isset($meta[$attr]) && intval($meta[$attr]) > 0) {
                $list[] = intval($meta[$attr]);
            }
        }
        return $list;
    }

    public static function getNameAttrList() { return self::getAttributeList("name"); }
    public static function getAddress1AttrList() { return self::getAttributeList("address1"); }
    public static function getAddress2AttrList() { return self::getAttributeList("address2"); }
    public static function getZipAttrList() { return self::getAttributeList("zip"); }
    public static function getCityAttrList() { return self::getAttributeList("city"); }
    public static function getEmailAttrList() { return self::getAttributeList("email"); }
    public static function getPhoneAttrList() { return self::getAttributeList("phone"); }

    public static function getShopValueAlias($shopid) {
        if(!isset(self::$metadata[$shopid])) return "";
        return self::$metadata[$shopid]["valuealias"];
    }

    public static function getShopCountry($shopid) {
        if(!isset(self::$metadata[$shopid])) return "";
        return self::$metadata[$shopid]["country"];
    }

    public static function getShopLangCode($shopid) {
        if(!isset(self::$metadata[$shopid])) return 1;
        return self::$metadata[$shopid]["lang_num"];
    }

    public static function validShopID($shopid) {
        return isset(self::$metadata[$shopid]);
    }

    public static function getMetadata($shopid) {
        return self::validShopID($shopid) ? self::$metadata[$shopid] : null;
    }

